import React, { useState } from 'react';
import './Signup.css'; // Make sure this path is correct

const SignupForm = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [farmName, setFarmName] = useState('');
  const [farmAddress, setFarmAddress] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [agreeTerms, setAgreeTerms] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically handle form submission
    // For example, you might send this data to a backend server for processing
    // After successful submission, you may want to clear the form fields
    clearForm();
  };

  const clearForm = () => {
    setFullName('');
    setEmail('');
    setPhoneNumber('');
    setFarmName('');
    setFarmAddress('');
    setPassword('');
    setConfirmPassword('');
    setAgreeTerms(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <h2>Sign Up for Farmer of our Local Farmer's Market!</h2>
        <label>
          Full Name:
          <input
            type="text"
            placeholder="Enter your full name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <label>
          Phone Number:
          <input
            type="tel"
            placeholder="Enter your phone number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
        </label>
        <label>
          Farm Name:
          <input
            type="text"
            placeholder="Enter your farm name"
            value={farmName}
            onChange={(e) => setFarmName(e.target.value)}
          />
        </label>
        <label>
          Farm Address:
          <input
            type="text"
            placeholder="Enter your farm address"
            value={farmAddress}
            onChange={(e) => setFarmAddress(e.target.value)}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            placeholder="Create a password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <label>
          Confirm Password:
          <input
            type="password"
            placeholder="Confirm your password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </label>
        <label>
          <input
            type="checkbox"
            checked={agreeTerms}
            onChange={() => setAgreeTerms(!agreeTerms)}
          />
          I agree to the terms and conditions
        </label>
        <button type="submit" disabled={!agreeTerms}>
          Sign Up
        </button>
        <p>
          Already have an account? <a href="/login">Login</a>
        </p>
      </div>
    </form>
  );
}

export default SignupForm;
