import React, { useState } from 'react';

const RegistrationForm = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
  }

  return (
    <form onSubmit={handleSubmit} style={{ backgroundColor: '#dff0d8', padding: '20px' }}>
      <h2 style={{ color: '#2e7d32' }}>Sign Up for our Local Farmer's Market!</h2>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Enter a full name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%', borderRadius: '5px', border: '1px solid #ccc' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        <input
          type="email"
          placeholder="Enter an email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%', borderRadius: '5px', border: '1px solid #ccc' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        <input
          type="tel"
          placeholder="Enter a phone number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%', borderRadius: '5px', border: '1px solid #ccc' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        <input
          type="password"
          placeholder="Create a password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%', borderRadius: '5px', border: '1px solid #ccc' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        <input
          type="password"
          placeholder="Confirm your password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%', borderRadius: '5px', border: '1px solid #ccc' }}
        />
      </label>
      <button type="submit" style={{ backgroundColor: '#2e7d32', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginBottom: '10px' }}>
        Sign Up
      </button>
      <p style={{ marginTop: '10px' }}>
        Already have an account? <a href="/login" style={{ color: '#2e7d32' }}>Login</a>
      </p>
    </form>
  );
}

export default RegistrationForm;
