import React, { useState } from 'react';

const RegistrationForm = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
  }

  return (
    <form onSubmit={handleSubmit} style={{ backgroundColor: '#dff0d8', padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '100vh' }}>
      <div style={{ backgroundImage: `url("https://source.unsplash.com/random/800x600")`, backgroundSize: 'cover', height: '100%', width: '100%', position: 'absolute', top: 0, left: 0, zIndex: -1 }} />
      <div style={{ backgroundColor: 'rgba(255, 255, 255, 0.8)', padding: '20px', borderRadius: '5px', width: '200px', display: 'flex', flexDirection: 'column' }}>
        <h2 style={{ color: '#2e7d32', textAlign: 'center', marginBottom: '10px' }}>Sign Up</h2>
        <label style={{ display: 'block', marginBottom: '5px' }}>
          <input
            type="text"
            placeholder="Name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            style={{ marginTop: '5px', padding: '5px', width: '100%', fontSize: '14px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
        </label>
        <label style={{ display: 'block', marginBottom: '5px' }}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ marginTop: '5px', padding: '5px', width: '100%', fontSize: '14px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
        </label>
        <label style={{ display: 'block', marginBottom: '5px' }}>
          <input
            type="tel"
            placeholder="Phone"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            style={{ marginTop: '5px', padding: '5px', width: '100%', fontSize: '14px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
        </label>
        <label style={{ display: 'block', marginBottom: '5px' }}>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ marginTop: '5px', padding: '5px', width: '100%', fontSize: '14px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
        </label>
        <label style={{ display: 'block', marginBottom: '5px' }}>
          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            style={{ marginTop: '5px', padding: '5px', width: '100%', fontSize: '14px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
        </label>
        <button type="submit" style={{ backgroundColor: '#2e7d32', color: 'white', padding: '5px 10px', fontSize: '14px', border: 'none', borderRadius: '5px', cursor: 'pointer', marginBottom: '10px' }}>
          Sign Up
        </button>
        <p style={{ marginTop: '10px', textAlign: 'center' }}>
          Already have an account? <a href="/login" style={{ color: '#2e7d32' }}>Login</a>
        </p>
      </div>
    </form>
  );
}

export default RegistrationForm;c
