import React, { useState } from 'react';

const RegistrationForm = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
  }

  return (
    <form onSubmit={handleSubmit} style={{ backgroundColor: '#dff0d8', padding: '20px' }}>
      <h2 style={{ color: '#2e7d32' }}>Sign Up for our Local Farmer's Market!</h2>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        <div style={{ position: 'relative', width: '50px', height: '50px', borderRadius: '50%', backgroundColor: '#dff0d8', overflow: 'hidden' }}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ position: 'absolute', top: '0', left: '0', width: '100%', height: '100%', border: 'none', padding: '10px', boxSizing: 'border-box', fontSize: '16px' }}
          />
        </div>
        Enter a username
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        Enter your email:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        Enter your phone number:
        <input
          type="tel"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        Create a password:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%' }}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '10px' }}>
        Confirm your password:
        <input
          type="password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          style={{ marginTop: '5px', padding: '5px', width: '100%' }}
        />
      </label>
      <button type="submit" style={{ backgroundColor: '#2e7d32', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
        Sign Up
      </button>
      <p style={{ marginTop: '10px' }}>
        Already have an account? <a href="/login" style={{ color: '#2e7d32' }}>Login</a>
      </p>
    </form>
  );
}

export default RegistrationForm;
